import React, { useState } from 'react';
import '../meetstyle/File.css';
import 'react-pdf/dist/esm/Page/AnnotationLayer.css';
import { Document, Page, pdfjs } from "react-pdf";
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;

const PDFview = ({ prfUrl }) => {
    const [pageNumber, setPageNumber] = useState(1);
    const [pageNumberInput, setPageNumberInput] = useState(1);
    const [pageNumberFocus, setPageNumberFocus] = useState(false);
    const [numPages, setNumPages] = useState(1);
    const [pageWidth, setPageWidth] = useState(600);
    const [fullscreen, setFullscreen] = useState(false);
  
    const lastPage = () => {
      if (pageNumber === 1) {
        return;
      }
      setPageNumber(pageNumber - 1);
      setPageNumberInput(pageNumber - 1);
    };
  
    const nextPage = () => {
      if (pageNumber === numPages) {
        return;
      }
      setPageNumber(pageNumber + 1);
      setPageNumberInput(pageNumber + 1);
    };
  
    const onPageNumberFocus = () => {
      setPageNumberFocus(true);
    };
  
    const onPageNumberBlur = () => {
      setPageNumberFocus(false);
      setPageNumberInput(pageNumber);
    };
  
    const onPageNumberChange = (e) => {
      let value = e.target.value;
      value = value <= 0 ? 1 : value;
      value = value >= numPages ? numPages : value;
      setPageNumberInput(value);
    };
    const toPage = (e) => {
        if (e.keyCode === 13) {
          setPageNumber(Number(e.target.value));
        }
      };
      return (
      
            <div className="container">
            <Document
                file={prfUrl}
                onLoadSuccess={this.onDocumentLoadSuccess}
                loading={"加载中..."}
                renderTextLayer={false}
            >
                <Page
                    pageNumber={pageNumber}
                    width={pageWidth}
                    loading={"加载中..."}
                />
            </Document>
        
        
            <div className="page-tool">
                        <div className="page-tool-item" onClick={this.lastPage}>
                            {" "}
                            上一页
                        </div>
                        <div className="page-tool-item" onClick={this.nextPage}>
                            {" "}
                            下一页
                        </div>
                        <div className="input">
                            <input
                                value={pageNumberFocus ? pageNumberInput : pageNumber}
                                onFocus={this.onPageNumberFocus}
                                onBlur={this.onPageNumberBlur}
                                onChange={this.onPageNumberChange}
                                onKeyDown={this.toPage}
                                type="number"
                            />{" "}
                            / {numPages}
                        </div>
                        <div className="page-tool-item" onClick={this.pageZoomIn}>
                            {" "}
                            放大
                        </div>
                        <div className="page-tool-item" onClick={this.pageZoomOut}>
                            {" "}
                            缩小
                        </div>
                        <div className="page-tool-item" onClick={this.pageFullscreen}>
                            {fullscreen ? "恢复默认" : "适合窗口"}
                        </div>
                    </div>
    
            </div>
      );
}
export default PDFview;
