import request from "request";
export async function uploadimage(data) {
    try {
        const response = request({
            url: "/api/upload",
            method: "POST",
            data,
            noAuth: false,
        });
        return response.data;
      } catch (err) {
        console.error(err);
        throw err;
      }
}
