import React, { useState } from "react";
import axios from "axios";

function ImageUploadPage() {
  const [selectedImages, setSelectedImages] = useState([]);

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    const acceptedImageTypes = ["image/jpeg", "image/png", "image/gif"];
    const maxFileSize = 10 * 1024 * 1024; // 10MB
    const selectedValidImages = files.filter(
      (file) =>
        acceptedImageTypes.includes(file.type) && file.size <= maxFileSize
    );

    setSelectedImages([...selectedImages, ...selectedValidImages]);
  };

  const handleDeleteImage = (index) => {
    const updatedImages = [...selectedImages];
    updatedImages.splice(index, 1);
    setSelectedImages(updatedImages);
  };

  return (
    <div className="container">
      <h1 className="title">图片上传</h1>
      <div className="upload-container">
        <label className="upload-label" htmlFor="upload-input">
          选择图片
        </label>
        <input
          id="upload-input"
          type="file"
          multiple
          accept="image/jpeg,image/png,image/gif"
          onChange={handleImageChange}
        />
      </div>
      <div className="image-preview-container">
        {selectedImages.map((image, index) => (
          <div className="image-preview" key={index}>
            <div
              className="delete-overlay"
              onClick={() => handleDeleteImage(index)}
            >
              <i className="fas fa-times"></i>
            </div>
            <img className="preview-image" src={URL.createObjectURL(image)} alt="preview" />
          </div>
        ))}
      </div>
    </div>
  );
};
export default ImageUploadPage;
