import request from "./request";

// 请求上传图片接口
export const uploadimage = (data) => { 
  return request.post('/api/upload',data,{headers:{ 'Content-Type': 'multipart/form-data'}})
};

// 请求pdf文件
export const requestPDF = (data) => { 
  return request.post('/api/requestPDF')
};
