//     console.log(data);
//     return (
//       <div className="custom-annotation-layer">
//         {data.map((annotation) => (
//           <div
//             key={annotation.id}
//             className="custom-annotation"
//             style={{
//               left: `${annotation.x}%`,
//               top: `${annotation.y}%`,
//               width: `${annotation.width}%`,
//               height: `${annotation.height}%`,
//             }}
//           >
//             {annotation.text}
//           </div>
//         ))}
//       </div>
//     );
//   };

export default PDFview;
