import React, { useState } from "react";
import axios from "axios";
import "../meetstyle/ImageUploader.css";

function ImageUploader() {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [previewImages, setPreviewImages] = useState([]);

  const handleFileInput = (e) => {
    setSelectedFiles([...selectedFiles, ...e.target.files]);
  };

  const handleRemove = (fileToRemove) => {
    const filteredFiles = selectedFiles.filter((file) => file !== fileToRemove);
    setSelectedFiles(filteredFiles);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();

    for (let i = 0; i < selectedFiles.length; i++) {
      formData.append("images", selectedFiles[i]);
    }

    try {
      await axios.post("http://your-api-endpoint.com/upload", formData);
      alert("Images uploaded successfully!");
    } catch (err) {
      console.error(err);
      alert("Error uploading images");
    }
  };

  return (
    <div className="uploader-container">
      <form onSubmit={handleSubmit}>
      <div className="file-upload">
        <label htmlFor="imageUpload" >预览图片</label>
        <br></br>
        <input
          type="file"
          id="imageUpload"
          name="imageUpload"
          accept=".jpg, .jpeg, .png"
          multiple
          onChange={handleFileInput}
        />
        {previewImages.length > 0 &&
          previewImages.map((imgUrl) => (
            <img src={imgUrl} key={imgUrl} alt="Preview" style={{ maxWidth: 200 }} />
          ))}
        </div>
        <button type="submit" className="upload-button">
          上传
        </button>
      </form>
      
      <div className="selected-images-container">
        {selectedFiles.map((file) => (
          <div className="image-preview" key={file.name}>
            <img src={URL.createObjectURL(file)} alt={file.name} className="image-preview" />
            <button className="delete-image" onClick={() => handleRemove(file)}>
              x
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ImageUploader;
