import React, { useState } from "react";
import axios from "axios";
import "../meetstyle/ImageUploader.css"
function ImageUploader() {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [previewImages, setPreviewImages] = useState([]);

  const handleFileInput = (e) => {
    setSelectedFiles(e.target.files);

    const previewImages = [];
    for (let i = 0; i < e.target.files.length; i++) {
      const file = e.target.files[i];
      previewImages.push(URL.createObjectURL(file));
    }
    setPreviewImages(previewImages);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();

    for (let i = 0; i < selectedFiles.length; i++) {
      formData.append("images", selectedFiles[i]);
    }
    const handleDelete = (index) => {
      const newFiles = [...selectedFiles];
      newFiles.splice(index, 1);
      setSelectedFiles(newFiles);
    };
    try {
      await axios.post("http://your-api-endpoint.com/upload", formData);
      alert("Images uploaded successfully!");
    } catch (err) {
      console.error(err);
      alert("Error uploading images");
    }
  };

  return (
    <div className="image-uploader-container">
      <form onSubmit={handleSubmit}>
      <div className="file-upload">
        <label htmlFor="imageUpload">Select images:</label>
        <input
          type="file"
          id="imageUpload"
          name="imageUpload"
          accept=".jpg, .jpeg, .png"
          multiple
          onChange={handleFileInput}
        />
        
        {previewImages.length > 0 &&
          previewImages.map((imgUrl) => (
            <img src={imgUrl} key={imgUrl} alt="Preview" style={{ maxWidth: 200 }} />
          ))}
          </div>
        <button type="submit">Submit</button>
      </form>
      <div className="selected-images-container">
        {selectedFiles.map((file, index) => (
          <div className="selected-image" key={index}>
            <img
              src={URL.createObjectURL(file)}
              alt={`Image ${index + 1}`}
              className="image-preview"
            />
            <button
              className="delete-image"
              onClick={() => handleDelete(index)}
            >
              X
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ImageUploader;
