import React, { useState } from "react";
import "../meetstyle/ImageUploader.css"
import { uploadimage } from "../api/upload";
function ImageUploader() {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [isDisabled, setIsDisabled] = useState(true); 
  const text="";
  const handleFileInput = (e) => {
    setSelectedFiles([...selectedFiles, ...e.target.files]);
    setIsDisabled(false);
  };

  const handleRemove = (index) => {
    const newFiles = [...selectedFiles];
    newFiles.splice(index, 1);
    setSelectedFiles(newFiles);  
    if (newFiles.length === 0) { // disable button when all files are removed
       this.text="请选择要上传的图片！"
      setIsDisabled(true);  
      
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try{
      const formData = new FormData();
      for (let i = 0; i < selectedFiles.length; i++) {
        formData.append("image", selectedFiles[i]);
    }

    console.log(formData);
      const result=await uploadimage(formData)
      console.log(result,"rresult");
      if (result.code===200) {
        alert(result.msg)
        // 上传成功清除图片
        setSelectedFiles([])
        setIsDisabled(true); // disable button after successful upload
        return result;
      }else{
        alert(result.msg)
        return result;
      }
      
     
    }catch(err){
      console.error(err);
      alert("错误！！");
    }
  }
  return (
    <div className="uploader-container">
      <div className="upload-form-container" >
        <form onSubmit={handleSubmit}>
          <label htmlFor="imageUpload">选择图片</label>
          <input
            type="file"
            id="imageUpload"
            name="imageUpload"
            accept=".jpg, .jpeg, .png"
            multiple
            onChange={handleFileInput}
          />

          <button type="submit" className="upload-button" disabled={isDisabled}>提交</button> {/* disable button when no files are selected */}
        </form>
      </div>
      <div className="selected-images-container">
        <label>{text}</label>
        {selectedFiles.map((file, index) => (
          <div className="selected-image-container" key={index}>
            <img
              className="selected-image"
              src={URL.createObjectURL(file)}
              alt="selected"    
            />
            <button
              className="remove-button"
              onClick={() => handleRemove(index)}
            >
              x
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ImageUploader;
