import React ,{ useState } from 'react';
import "../meetstyle/uploadimage.css"
import axios from 'axios';


function Uploadimage() {
  const [images, setImages] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');

  const handleImageChange = (event) => {
    if (event.target.files) {
      const files = Array.from(event.target.files);
      let error = false;
      const newImages = files.map((file) => {
        if (file.size > 10 * 1024 * 1024) {
          error = true;
          setErrorMessage('文件大小不能超过10MB');
        } else if (!file.type.startsWith('image/')) {
          error = true;
          setErrorMessage('只能上传图片文件');
        } else {
          return URL.createObjectURL(file);
        }
      });
      if (!error) {
        setErrorMessage('');
        setImages((prevImages) => [...prevImages, ...newImages]);
      }
    }
  };

  const handleRemoveImage = (index) => {
    setImages((prevImages) => prevImages.filter((_, i) => i !== index));
  };

  const handleUploadImages = () => {
    const formData = new FormData();
    images.forEach((image) => {
      formData.append('images', image);
    });
    axios.post('/api/upload', formData).then((response) => {
      console.log(response.data);
    });
  };

  return (
    <div className="container">
      <h1 className="title">图片上传页面</h1>
      <div className="input-wrapper">
        <input type="file" accept="image/*" multiple onChange={handleImageChange} />
      </div>
      {errorMessage && <div className="error-message">{errorMessage}</div>}
      {images.length > 0 && (
        <div className="image-list">
          {images.map((image, index) => (
            <div className="image-container" key={index}>
              <img src={image} alt="preview" className="image-preview" />
              <button onClick={() => handleRemoveImage(index)} className="image-delete-button">删除</button>
            </div>
          ))}
          <button onClick={handleUploadImages} className="upload-button">上传</button>
        </div>
      )}
    </div>
  );
}

export default Uploadimage;
