import React, { useState } from "react";
import axios from "axios";
import "../meetstyle/ImageUploader.css"
function ImageUploader() {
  const [selectedFiles, setSelectedFiles] = useState([]);

  const handleFileInput = (e) => {
    setSelectedFiles([...selectedFiles, ...e.target.files]);
  };

  const handleDelete = (index) => {
    const newFiles = [...selectedFiles];
    newFiles.splice(index, 1);
    setSelectedFiles(newFiles);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();

    for (let i = 0; i < selectedFiles.length; i++) {
      formData.append("images", selectedFiles[i]);
    }

    try {
      await axios.post("http://your-api-endpoint.com/upload", formData);
      alert("Images uploaded successfully!");
    } catch (err) {
      console.error(err);
      alert("Error uploading images");
    }
  };

  return (
    <div className="image-uploader">
      <form onSubmit={handleSubmit}>
        <div className="image-uploader">
        <label htmlFor="imageUpload" className="upload-label">
          Select images:
        </label>
        <input
          type="file"
          id="imageUpload"
          name="imageUpload"
          accept=".jpg, .jpeg, .png"
          multiple
          onChange={handleFileInput}
        />
        </div>
        <button type="submit" className="upload-button">
          Upload
        </button>
      </form>
      <div className="image-preview-container">
        {selectedFiles.map((file, index) => (
          <div className="image-preview-wrapper" key={index}>
            <img src={URL.createObjectURL(file)} alt="preview" className="image-preview" />
            <button className="delete-button" onClick={() => handleDelete(index)}>
              <i className="fas fa-times"></i>
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ImageUploader;
